package gotrax

import (
	"time"
)

func generateSitaClientProfile(longTerm *Keypair) *ClientProfile {
	sita := &ClientProfile{}
	sita.InstanceTag = 0x1245ABCD
	sita.PublicKey = longTerm.Pub
	sita.Versions = []byte{'4'}
	sita.Expiration = time.Date(2028, 11, 5, 13, 46, 00, 13, time.UTC)
	sita.DsaKey = nil
	sita.TransitionalSignature = nil
	sita.Sig = &EddsaSignature{s: [114]byte{
		0xc9, 0x85, 0x49, 0xd5, 0x46, 0x92, 0xde, 0x73, 0xe4, 0x5d, 0xc1, 0xdb, 0x7e, 0x8e, 0x3c, 0x3d, 0x58, 0x22, 0xea, 0xe9, 0x35, 0xc9, 0xf8, 0xcb, 0x55, 0xb0, 0x78, 0x40, 0x6a, 0x9d, 0xaa, 0x8a, 0x52, 0xc7, 0xf2, 0xb3, 0xfb, 0xb0, 0xb3, 0x23, 0x7c, 0xf1, 0xf2, 0xfe, 0xbc, 0x82, 0x70, 0x65, 0x0c, 0x39, 0x9f, 0x46, 0xd7, 0x85, 0xf0, 0xae, 0x80, 0x27, 0x9a, 0x55, 0x78, 0x23, 0xf1, 0x1c, 0x3b, 0x97, 0x71, 0x0a, 0x7b, 0xfe, 0x5a, 0x05, 0xec, 0x18, 0x1a, 0x1e, 0xa8, 0x09, 0xc7, 0xcf, 0x22, 0x0d, 0x9f, 0x03, 0x83, 0xb3, 0xc4, 0xf2, 0x42, 0x2c, 0xee, 0xf9, 0xf6, 0x39, 0x95, 0x60, 0x7b, 0x9a, 0x8c, 0xf5, 0xd2, 0xab, 0xf0, 0x69, 0x93, 0x6a, 0x53, 0x90, 0xb0, 0xd5, 0xff, 0x73, 0x1a, 0x00,
	}}
	return sita
}

func generateSitaIPoint() *Keypair {
	return DeriveKeypair([SymKeyLength]byte{0x42, 0x11, 0xCC, 0x22, 0xDD, 0x11, 0xFF})
}

type testData struct {
	instanceTag   uint32
	longTerm      *Keypair
	clientProfile *ClientProfile
	i             *Keypair
}

func generateSitaTestData() *testData {
	t := &testData{}
	t.instanceTag = 0x1245ABCD
	t.longTerm = DeriveKeypair([SymKeyLength]byte{0x42, 0x00, 0x00, 0x55, 0x55, 0x00, 0x00, 0x55})
	t.clientProfile = generateSitaClientProfile(t.longTerm)
	t.i = generateSitaIPoint()
	return t
}

var sita = generateSitaTestData()
